import React, { useState } from 'react';
import './App.css';
import backgroundImage from './app.jpg'; // Replace with your actual image path

function App() {
  const [rating, setRating] = useState(3);

  return (
    <div className="App">
      <header className="App-header">
        {/* Left Section - Full Height Image */}
        <div className="left-section">
          <img src={backgroundImage} alt="Background" className="full-height-image" />
        </div>

        {/* Right Section - Purple Background with Form */}
        <div className="right-section">
          <p className="App-title">Team Movies</p>
          <form className="question-form">
            <label>
              First Name:
              <input type="text" name="first_name" />
            </label>
            <label>
              Last Name:
              <input type="text" name="last_name" />
            </label>
            <label>
              Rank these movies: <span className="rating-value">{rating}</span>
              <div className="slider-container">
                <span className="slider-label">1</span>  {/* Left label */}
                <input
                  type="range"
                  min="1"
                  max="5"
                  value={rating}
                  onChange={(e) => setRating(e.target.value)}
                  className="slider"
                />
                <span className="slider-label">5</span>  {/* Right label */}
              </div>
            </label>
            <button type="submit">Submit</button>
          </form>
        </div>
      </header>
    </div>
  );
}

export default App;
